    requireInteraction: true,
    vibrate: [1000, 500, 1000, 500, 1000, 500, 1500],
    silent: false,
    renotify: true,
    timestamp: Date.now(),
    persistent: true,
    dir: 'auto',
    lang: 'en',
    actions: [
      {
        action: 'start-rest',
        title: 'Start Rest',
